CREATE TABLE `shop_recommend_goods`  (
  `id` mediumint(8) UNSIGNED NOT NULL AUTO_INCREMENT,
  `key` char(6) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '唯一标识符',
  `merchant_id` int(11) NOT NULL COMMENT '商户id',
  `supplier_id` int(10) NOT NULL DEFAULT 0 COMMENT '供应商id，0为系统',
  `config` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '推荐商品信息(json)',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL COMMENT '修改时间',
  `delete_time` int(11) DEFAULT NULL COMMENT '删除时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 2 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '推荐商品表' ROW_FORMAT = Compact;
